choose.when:
SELECT YEAR(RQTJ) AS SJ,NVL(SUM(SCBQSJS),0) DQZBZ FROM "APP"."APP_AJLXFX_QSQK" WHERE CBDW_BM LIKE #{dwbm}||'%'
AND AJLX = #{ajlx}
GROUP BY YEAR(RQTJ) ORDER BY YEAR(RQTJ)

SELECT MONTH(RQTJ) AS SJ,SUM(CASE WHEN RQTJ BETWEEN #{dqqssj} AND #{dqjzsj} THEN SCBQSJS ELSE 0 END) DQZBZ, SUM(CASE WHEN RQTJ BETWEEN #{tqqssj} AND #{tqjzsj} THEN SCBQSJS ELSE 0 END) TQZBZ FROM "APP"."APP_AJLXFX_QSQK" WHERE ((RQTJ BETWEEN #{dqqssj} AND #{dqjzsj}) OR (RQTJ BETWEEN #{tqqssj} AND #{tqjzsj})) AND CBDW_BM LIKE #{dwbm}||'%'
AND AJLX = #{ajlx}
GROUP BY MONTH(RQTJ) ORDER BY MONTH(RQTJ)

mapper.select:
SELECT NVL(SUM(1),0) FROM ADM.ADM_XSZT_SCQS WHERE SLRQ BETWEEN #{ksrq} AND #{jzrq} AND CBDW_BM LIKE #{dwbm}||'%' AND SHSE_BQSS > 0
AND YSAY_AYMC LIKE '%'||#{gjz}||'%'
AND AJLX = #{ajlx}

SELECT YSAY_AYMC AS AJXZ, SUM(CASE WHEN SLRQ BETWEEN #{ksrq} AND #{jzrq} THEN 1 ELSE 0 END) ZBZ, SUM(CASE WHEN SLRQ BETWEEN #{tqksrq} AND #{tqjzrq} THEN 1 ELSE 0 END) TQZBZ FROM ADM.ADM_XSZT_SCQS WHERE ((SLRQ BETWEEN #{ksrq} AND #{jzrq}) OR (SLRQ BETWEEN #{tqksrq} AND #{tqjzrq})) AND CBDW_BM LIKE #{dwbm}||'%' AND SHSE_BQSS > 0
AND YSAY_AYMC LIKE '%'||#{gjz}||'%'
AND AJLX = #{ajlx}
GROUP BY YSAY_AYMC ORDER BY SUM(CASE WHEN SLRQ BETWEEN #{ksrq} AND #{jzrq} THEN 1 ELSE 0 END) DESC LIMIT #{beginNum},#{pageSize}

SELECT BMSAH,AJMC,TO_DATE(SJRQ,'YYYY-MM-DD') AS SJRQ FROM ADM.ADM_XSZT_SCQS WHERE SLRQ BETWEEN #{ksrq} AND #{jzrq} AND CBDW_BM LIKE #{dwbm}||'%' AND SHSE_BQSS > 0
AND BMSAH LIKE '%'||#{bmsah}||'%'
AND AJMC LIKE '%'||#{ajmc}||'%'
AND AJLX = #{ajlx}
LIMIT #{beginNum},#{pageSize}

SELECT BMSAH,AJMC,SLRQ,SJRQ,CBDW_MC AS CBDW,DATA_SRC  AS AJLY,AQZY,YSAY_AYMC AS AY,TYSAH
FROM "ADM"."ADM_XSZT_SCQS"
WHERE BMSAH = #{bmsah}
